int main(){
	int n;
	while(scanf("%d",&n),n){
		bool stage[22][22]={{0}};
		for(int i = 0; i < n; i++){
			int hx,hy;
			scanf("%d%d",&hx,&hy);
			stage[hy][hx] = 1;
		}
		int m;
		scanf(" %d",&m);
		int nx = 10;
		int ny = 10;
		int gotten = 0;
		for(int i = 0; i < m; i++){
			if(i == 0){
				stage[10][10] == 1 ? gotten = 1 : gotten = 0;
			}
			char dir;
			int dist;
			cin >> dir >> dist;
			int fx = nx;
			int fy = ny;
			if(dir == 'N'){
				fy = ny+dist;
				while(ny != fy)if(stage[++ny][nx] == 1)gotten++;
			}
			if(dir == 'E'){
				fx = nx+dist;
				while(nx != fx)if(stage[ny][++nx] == 1)gotten++;
			}
			if(dir == 'S'){
				fy = ny-dist;
				while(ny != fy)if(stage[--ny][nx] == 1)gotten++;
			}
			if(dir == 'W'){
				fx = fx-dist;
				while(nx != fx)if(stage[ny][--nx] == 1)gotten++;
			}
		}
		if(stage[ny][nx] == 1)gotten++;
		if(gotten == n)puts("Yes");
		else puts("No");
	}
	return 0;
}

