int N,M,ground[21][21];
int nowx=10,nowy=10;
int move(int x,int y,int l){
    int gathered=0;
    for(int i=0;i<=l;i++){
        if(ground[nowx+i*x][nowy+i*y]==1){
            gathered++;
            ground[nowx+i*x][nowy+i*y]=0;
        }
    }
    nowx+=l*x;
    nowy+=l*y;
    return gathered;
}
int solve(){
    int l;
    char d;
    nowx=10;nowy=10;
    for(int i=0;i<M;i++){
        cin >> d >> l;
        switch(d){
            case 'N':
                N-=move(0,1,l);
                break;
            case 'S':
                N-=move(0,-1,l);
                break;
            case 'E':
                N-=move(1,0,l);
                break;
            case 'W':
                N-=move(-1,0,l);
                break;
        }
    }
//    cout << N << endl;
    int ret;
    if(N==0)
        ret=0;
    else 
        ret=1;
    return ret;
}

int main(){
    for(;;){
        cin >> N;
        if(N==0)
            break;
        int xi,yi;
        for(int i=0;i<N;i++){
            cin >> xi >> yi;
            ground[xi][yi]=1;
        }
        cin >> M;
        if(solve()==0)
            cout << "Yes" << endl;
        else
            cout << "No" << endl;
    }
    return 0;
}

