using namespace std;

string solve(int n) {
    string ans;
    int treasurePosition[n][2];
    int move;
    char movedirection[move];
    int cast[move];

    for (int i = 0; i < n; i++) {
        cin >> treasurePosition[i][0] >> treasurePosition[i][1];
    }
    cin >> move;
    for (int i = 0; i < move; i++) {
        cin >> movedirection[i] >> cast[i];
    }
    int xPosition = 10;
    int yPosition = 10;
    int count     = 0;
    for (int i = 0; i < n; i++) {
        // int cast = (int)movedirection[i][1];
        for (int j = 0; j < cast[i]; j++) {
            if (movedirection[i] == 'N') {
                yPosition++;
            } else if (movedirection[i] == 'E') {
                xPosition++;
            } else if (movedirection[i] == 'S') {
                xPosition--;
            } else if (movedirection[i] == 'W') {
                yPosition--;
            }
            for (int k = 0; k < n; k++) {
                if (yPosition == treasurePosition[j][0] && xPosition == treasurePosition[j][1]) count++;
            }
        }
    }
    if (count == n)
        return "Yes";
    else
        return "No";
}

int main(int argc, char const *argv[]) {
    int n;
    vector<string> ans;
    while (1) {
        cin >> n;
        if (n == 0) {
            break;
        }
        ans.push_back(solve(n));
    }
    for (int i = 0; i < ans.size(); i++) {
        cout << ans[i] << endl;
    }
    return 0;
}

