using namespace std;

void take(vector<vector<bool>> &m, int &x, int &y, char dir, int dis){
	m[x][y] = false;
	switch(dir) {
		case'N':
			for(int i = 0; i < dis; i++){
				m[x][++y] = false;
			}
			break;
		case'S':
			for(int i = 0; i < dis; i++){
				m[x][--y] = false;
			}
			break;
		case'E':
			for(int i = 0; i < dis; i++){
				m[++x][y] = false;
			}
			break;
		case'W':
			for(int i = 0; i < dis; i++){
				m[--x][y] = false;
			}
			break;
	}
	m[x][y] = false;
}

