				case 'S': dy=-1; break;
				case 'W': dx=-1; break;
			}
			rep(j, a) {
				now.first += dx;
				now.second += dy;

				if (gems[P(now.first, now.second)])
					got++;
			}
		}
		if (got==N)
			cout << "Yes" << endl;
		else
			cout << "No" << endl;
	}
}

