        pos.Y -= x;
      }else if(S[0] == 'W'){
        while(x--){
          pos.X--;
          cell[pos.X][pos.Y] = 0;
        }
      }
    }
    bool flag = false;
    rep(i,32)rep(j,32)
      if(cell[i][j] != 0)
        flag |=true;

    if(flag) cout << "No" << endl;
    else cout << "Yes" << endl;
  }

  return 0;
}

