typedef struct P {
	int x, y;
	bool ok;
	bool operator==(P other) {
		return x == other.x && y == other.y;
	}
	P operator+=(P other) {
		x += other.x;
		y += other.y;
		return{ x + other.x,y + other.y };
	}
}P;
typedef struct D {
	char d;
	int l;
}D;

int n, m;
P loc, p[20];
D o;

P get_e(D a) {
	P e = { 0,0 };
	switch (a.d) {
	case 'W':e = { -1,0 }; break;
	case 'N':e = { 0,1 }; break;
	case 'E':e = { 1,0 }; break;
	case 'S':e = { 0,-1 }; break;
	default:break;
	}
	return e;
}

int find() {
	for (int i = 0; i < n; i++) {
		if (loc == p[i]) {
			p[i].ok = true;
		}
	}
	return 0;
}

int move(D a) {
	P e = get_e(a);
	for (int i = 0; i < a.l; i++) {
		loc += e;
		find();
	}
	return 0;
}

bool check() {
	for (int i = 0; i < n; i++) {
		if (!p[i].ok)
			return false;
	}
	return true;
}

