  if(d == 'W') {
    for(int i = 1; i <= l; ++i) {
      nx = nx - 1;
      pick(nx, ny);
    }
  }
  else if(d == 'E') {
    for(int i = 1; i <= l; ++i) {
      nx = nx + 1;
      pick(nx, ny);
    }
  }
  else if(d == 'N') {
    for(int i = 1; i <= l; ++i) {
      ny = ny + 1;
      pick(nx, ny);
    }
  }
  else {
    for(int i = 1; i <= l; ++i) {
      ny = ny - 1;
      pick(nx, ny);
    }
  }
}

