using P = std::pair<int, int>;

int main() {
    int N;
    while(cin >> N, N) {
        vector<vector<int>> jp;
        jp.resize(21);
        for(int i=0; i<jp.size(); ++i) {
            jp[i].resize(21);
        }
        for(int i=0; i<N; ++i) {
            int x, y;
            cin >> x >> y;
            jp[x][y] = 1;
        }
        int M;
        cin >> M;
        P p = make_pair(10, 10);
        for(int i=0; i<M; ++i) {
            char d;
            int l;
            cin >> d >> l;
            if(d == 'N') {
                for(int j=1; j<=l; ++j) {
                    jp[p.first][p.second+j] = 0;
                }
                p.second += l;
            } else if(d == 'E') {
                for(int j=1; j<=l; ++j) {
                    jp[p.first+j][p.second] = 0;
                }
                p.first += l;
            } else if(d == 'S') {
                for(int j=1; j<=l; ++j) {
                    jp[p.first][p.second-j] = 0;
                }
                p.second -= l;
            } else {
                for(int j=1; j<=l; ++j) {
                    jp[p.first-j][p.second] = 0;
                }
                p.first -= l;
            }
        }
        bool completed = true;
        for(int i=0; i<jp.size(); ++i) {
            for(int j=0; j<jp[i].size(); ++j) {
                if(jp[i][j] == 1) completed = false;
            }
        }
        cout << (completed ? "YES" : "NO") << endl;
    }
}

