map<char,int> mp; // <cmd, identifier>

int func(char cmd, int move){
  int ret = 0;

  mp['N'] = 0; mp['E'] = 1; mp['S'] = 2; mp['W'] = 3;

  while(move--){
    nx += dx[mp[cmd]];
    ny += dy[mp[cmd]];
    if(field[ny][nx] == 1){ ret++; field[ny][nx] = 0; }
  }

  return ret;
}

