#include <iostream>
#include <vector>
#include <algorithm>

int main(){
  int N;
  while(std::cin >> N, N){
    std::vector<int> x{},y{};
    x.reserve(N);
    y.reserve(N);
    int x_robo, y_robo;

    while(N--){
      std::cin >> x_robo >> y_robo;
      x.push_back(x_robo);
      y.push_back(y_robo);
    }

    x_robo = 10;
    y_robo = 10;

    int M;
    std::cin >> M;

    char d;
    int l;

    std::size_t pick_up = 0;
    while(M--){
      std::cin >> d >> l;
      switch(d){
      case 'S':
        l = -l;
        // No break
      case 'N':
        for(auto x_it = std::find(x.begin(),x.end(),x_robo); x.end() != x_it; x_it = std::find(std::next(x_it),x.end(),x_robo)){
          auto y_it = std::next(y.begin(),std::distance(x.begin(),x_it));
          if(y_robo <= (*y_it) && (*y_it) <= y_robo + l){ ++pick_up; }
        }
        y_robo += l;
        break;
      case 'W':
        l = -l;
        // No break
      case 'E':
        for(auto y_it = std::find(y.begin(),y.end(),y_robo); y.end() != y_it; y_it = std::find(std::next(y_it),y.end(),y_robo)){
          auto x_it = std::next(x.begin(),std::distance(y.begin(),y_it));
          if(x_robo <= (*x_it) && (*x_it) <= x_robo + l){ ++pick_up; }
        }
        x_robo += l;
        break;
      default:
        break;
      }
    }
    std::cout << ((x.size() == pick_up) ? "Yes" : "No") << std::endl;
  }
  return 0;
}

