#include <cstdio>
#include <iostream>
using namespace std;
int field[21][21];
int main() {
    int n;
    while (scanf("%d",&n)) {
        if (!n) break;
        for (int i=0; i<n; i++) {
            int x, y; scanf("%d%d",&x,&y);
            field[y][x]=1;
        }
        int m; scanf("%d",&m);
        int rx=10, ry=10;
        field[ry][rx]=0;
        for (int i=0; i<m; i++) {
            char d; cin>>d;
            int dist; scanf("%d",&dist);
            if (d=='N') {
                for (int j=ry; j<=ry+dist; j++) {
                    field[j][rx]=0;
                }
                ry+=dist;
            }
            if (d=='E') {
                for (int j=rx; j<=rx+dist; j++) {
                    field[ry][j]=0;
                }
                rx+=dist;
            }
            if (d=='S') {
                for (int j=ry; j>=ry-dist; j--) {
                    field[j][rx]=0;
                }
                ry-=dist;
            }
            if (d=='W') {
                for (int j=rx; j>=rx-dist; j--) {
                    field[ry][j]=0;
                }
                rx-=dist;
            }
        }
        int flag=0;
        for (int i=0; i<21; i++) {
            for (int j=0; j<21; j++) {
                flag+=field[i][j];
                field[i][j]=0;
            }
        }
        if (flag!=0) puts("NO");
        else puts("YES");
    }
}

