						data[roboX][roboY+i]=0;
					}
				}
				roboY+=length;
			}
			else if(dir=='S'){
				for(int i=0;i<=length;i++){
					if(data[roboX][roboY-i]==1){
						sum++;
						data[roboX][roboY-i]=0;
					}
				}
				roboY-=length;
