				else if(d == 'S') used[x][--y] = true;
				else if(d == 'E') used[++x][y] = true;
				else used[--x][y] = true;
			}
		}
		int cnt = 0;
		for(int i = 0;i <= 20;i++){
			for(int j = 0;j <= 20;j++){
				if(used[i][j]) cnt += field[i][j];
			}
		}
		if(cnt == n) cout << "Yes" << endl;
		else cout << "No" << endl;
	}
	return 0;
}

