bool solve(int N) {
    bool exist[21][21] = {};
    for (int i = 0; i < N; i++) {
        int x, y;
        cin >> x >> y;
        exist[x][y] = true;
    }
    int M;
    cin >> M;
    map<char, pair<int, int>> m;
    m['N'] = make_pair(0, 1);
    m['E'] = make_pair(1, 0);
    m['S'] = make_pair(0, -1);
    m['W'] = make_pair(-1, 0);
    int cx = 10, cy = 10;
    for (int i = 0; i < M; i++) {
        char d;
        int l;
        cin >> d >> l;
        for (int j = 0; j < l; j++) {
            if (exist[cx][cy]) {
                exist[cx][cy] = false;
            }
            cx += m[d].first;
            cy += m[d].second;
        }
        if (exist[cx][cy]) {
            exist[cx][cy] = false;
        }
    }
    bool res = true;
    for (int i = 0; i < 21; i++) {
        for (int j = 0; j < 21; j++) {
            if (exist[i][j]) {
                res = false;
            }
        }
    }
    return res;
}

int main() {
    int N;
    while (true) {
        cin >> N;
        if (!N) return 0;
        cout << (solve(N) ? "Yes" : "No") << endl;
    }
}

