#include<iostream>

using namespace std;

int main() {

    int N, M;

    while (1) {
        cin >> N;
        if (N == 0) break;
        int x, y;
        int grid[21][21];
        for (int i = 0; i < 21; ++i) {
            for(int j = 0; j < 21; ++j) {
                grid[i][j] = 0;
            }
        }

        for (int i = 0; i < N; ++i) {
            cin >> x >> y;
            grid[y][x] = 1;
        }

        cin >> M;

        int sx = 10, sy = 10;

        for (int i = 0; i < M; ++i) {
            char dj;
            int d;
            cin >> dj >> d;

            int dx, dy;
            if (dj == 'N') {
                dx = 0;
                dy = 1;
            } else if (dj == 'E') {
                dx = 1;
                dy = 0;
            } else if (dj == 'S') {
                dx = 0;
                dy = -1;
            } else if (dj == 'W') {
                dx = -1;
                dy = 0;
            }

            for (int j = 0; j < d; j++) {
                sx += dx;
                sy += dy;
                grid[sy][sx] = 0;
            } 
        }

        bool flag = true;
        for (int i = 0; i < 21; ++i) {
            for(int j = 0; j < 21; ++j) {
                if (grid[i][j] == 1) {
                    flag = false;
                }
            }
        }
        if (flag) {
            cout << "Yes" << endl;
        } else {
            cout << "No" << endl;
        }
    }
    return 0;
}

