    rx = i+1;
      }
      else if( dir == 'N' ){
    for(i=ry;i<=ry+step;i++){
      if( xy[i][rx] == 1 ) {
        count++;
        xy[i][rx] = 0;
      }
    }
    ry = i-1;
      }
      else if( dir == 'S' ){
    for(i=ry;i>=ry-step;i--){
      if( xy[i][rx] == 1 ) {
        count++;
        xy[i][rx] = 0;
      }
    }
    ry = i+1;
      }
    }
    if( count == gems ){
