        if (!N) { break; }

        for (int i = 0; i < 21; i++) {
            for (int j = 0; j < 21; j++) { table[i][j] = false; }
        }

        for (int i = 0; i < N; i++) {
            cin >> x >> y;
            table[x][y] = true;
        }

        x = 10; y = 10;
        cin >> M;

        while (M--) {
