				{
				case 'N': now_y += 1; break;
				case 'E': now_x += 1; break; 
				case 'S': now_y -= 1; break; 
				case 'W': now_x -= 1; break; 
				}
				ans += dat[now_x][now_y];
				//dat[now_x][now_y] = 0;
			}
		}
		if (ans == num) { printf("Yes\n"); }
