{
    while (1) {

        std::cin >> jems_num;
        if (jems_num == 0) {
            break;
        }

        clearJemsField();
        setJems(jems_num);

        Robot* robot = new Robot();

        std::cin >> orders_num;
        char order_direction[orders_num] = {};
        int order_distance[orders_num] = {};
        for (int i = 0; i < orders_num; i++) {
            std::cin >> order_direction[i];
            std::cin >> order_distance[i];
        }

        for (int i = 0; i < orders_num; i++) {
            robot->move(order_direction[i], order_distance[i]);
        }

        if (jems_num == 0) {
            std::cout << "Yes" << std::endl;
        } else {
            std::cout << "No" << std::endl;
        }
    }
    return 0;
}

