  }
  int M;
  cin >> M;
  REP(i,M){
    char c;
    int l;
    int d;
    cin >> c >> l;
    switch(c){
    case 'N':
      d = 0;
      break;
    case 'E':
      d = 1;
      break;
    case 'S':
      d = 2;
      break;
    case 'W':
      d = 3;
      break;
    }
    REP(j,l){
      x += dx[d];
      y += dy[d];
      if(map[x][y]){
	map[x][y] = false;
	N--;
      }
    }
  }
  if(N == 0) return true;
  else return false;
}

