char *ans[300];

//軸平行な線分と点の当たり判定
int Hit(int lx,int ly,int lx2,int ly2,int px,int py){
	if(lx <= px && px <= lx2 && ly <= py && py <= ly2)
		return 1;
	if(lx2 <= px && px <= lx && ly <= py && py <= ly2)
		return 1;
	if(lx <= px && px <= lx2 && ly2 <= py && py <= ly)
		return 1;
	if(lx2 <= px && px <= lx && ly2 <= py && py <= ly)
		return 1;
	return 0;
}
int main(){
	int N,M;
	int i,j;
	for(i = 0;cin >> N;i++)
	{
		if(N == 0)
			break;
		//ここからです
		int x[20],y[20],flag[20] = {0};
		int nowx = 10,nowy = 10;

		for(j = 0;j < N;j++)
		{
			cin >> x[j] >> y[j];
		}

		int k;
		cin >> M;
		for(j = 0;j < M;j++)
		{
			char c;
			int dx,dy = 0;
			cin >> c >> dx;
			switch(c){
			case 'N':
				dy = dx;
				dx = 0;
				break;
			case 'S':
				dy = -dx;
				dx = 0;
				break;
			case 'W':
				dx = -dx;
				break;
			}

			for(k = 0;k < N;k++)
			{
				if(flag[k])
					continue;
				//線分と点の当たり判定をする
				if(Hit(nowx,nowy,nowx+dx,nowy+dy,x[i],y[i]))
					flag[k]++;
			}
			nowx += dx;
			nowy += dy;
		}
		for(j = 0;j < N;j++){
			if(flag[j] == 0)
				break;
		}
		if(j == N){
			ans[i] = "Yes";
		}
		else
			ans[i] = "No";
	}

	for(j = 0;j < i;j++){
		cout << ans[j] << endl;
	}
}

