#define REP(i,b) for (int i=0;i<(b);i++)
#define FORj(j,a,b) for (int j=(a);j<=(b);j++)

int main(void) {

	int map[20][20] = { 0 };
	int rx = 10,ry = 10;
	int n, m, a, b;
	char dir;
	int cnt = 0;

	while (cin >> n,n) {

		REP(i,n) {
			scanf_s("%d %d", &a, &b);
			map[a][b] = 1;
		}

		scanf_s("%d" ,&m);
		REP(i,m) {
			cin >> dir >> a;

			switch (dir) {
			case 'N':
				FORj(j,1,a) {
					if (map[rx][ry + j] == 1)
						cnt++;
				}
				ry += a;
				break;

			case 'E':
				FORj(j, 1, a) {
					if (map[rx + j][ry] == 1)
						cnt++;
				}
				rx += a;
				break;

			case 'S':
				FORj(j, 1, a) {
					if (map[rx][ry - j] == 1)
						cnt++;
				}
				ry -= a;
				break;

			case 'W':
				FORj(j, 1, a) {
					if (map[rx - j][ry] == 1)
						cnt++;
				}
				rx -= a;
				break;
			}
		}

		if (cnt == n)
			cout << "Yes" << endl;
		else
			cout << "No" << endl;

		cnt = 0;
		rx = ry = 10;

		REP(i, 20) {
			REP(j, 20) {
				map[i][j] = 0;
			}
		}

	}
	return 0;
}

