#include <iostream>
#include <vector>
#include <algorithm>

int main(){
  int N;
  while(std::cin >> N, N){
    std::cout << N << std::endl;
    std::vector<int> x{},y{};
    x.reserve(N);
    y.reserve(N);
    int x_robo, y_robo;

    while(N--){
      std::cin >> x_robo >> y_robo;
      x.push_back(x_robo);
      y.push_back(y_robo);
    }

    x_robo = 10;
    y_robo = 10;

    int M;
    std::cin >> M;

    char d;
    int l;

    std::vector<int> x_pick_up{},y_pick_up{};
    while(M--){
      std::cin >> d >> l;
      switch(d){
      case 'S':
        l = -l;
        // No break
      case 'N':
        for(auto x_it = std::find(x.begin(),x.end(),x_robo); x.end() != x_it; x_it = std::find(std::next(x_it),x.end(),x_robo)){
          auto y_it = std::next(y.begin(),std::distance(x.begin(),x_it));
          if(y_robo <= (*y_it) && (*y_it) <= y_robo + l){ x_pick_up.push_back(*x_it); y_pick_up.push_back(*y_it); }
        }
        y_robo += l;
        break;
      case 'W':
        l = -l;
        // No break
      case 'E':
        for(auto y_it = std::find(y.begin(),y.end(),y_robo); y.end() != y_it; y_it = std::find(std::next(y_it),y.end(),y_robo)){
          auto x_it = std::next(x.begin(),std::distance(y.begin(),y_it));
          if(x_robo <= (*x_it) && (*x_it) <= x_robo + l){ x_pick_up.push_back(*x_it); y_pick_up.push_back(*y_it); }
        }
        x_robo += l;
        break;
      default:
        break;
      }
    }
    std::cout << ((std::is_permutation(x.begin(),x.end(),x_pick_up.begin()) &&
                   std::is_permutation(y.begin(),y.end(),y_pick_up.begin())) ? "Yes" : "No") << std::endl;
  }
  return 0;
}

