#include<iostream>
using namespace std;

bool ma[21][21];
int nowx, nowy;

void walk(char wc, int wl) {
	if (wc == 'N') {
		for (int i = 0; i < wl; i++) {
			nowy++;
			ma[nowx][nowy] = false;
		}
	}
	else if (wc == 'S') {
		for (int i = 0; i < wl; i++) {
			nowy--;
			ma[nowx][nowy] = false;
		}
	}
	else if (wc == 'E') {
		for (int i = 0; i < wl; i++) {
			nowx++;
			ma[nowx][nowy] = false;
		}
	}
	else {
		for (int i = 0; i < wl; i++) {
			nowx--;
			ma[nowx][nowy] = false;
		}
	}
}

int main() {
