using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using sc = Scanner;

class Program
{
    static void Main(string[] args)
    {
        var q = new AnswerCollecter<string>();

        while (true)
        {
            int n, m;
            int[][] jem, move;
            int x, y;
            int count = 0;
            n = sc.NextInt();
            jem = new int[n][];
            if (n == 0)
            {
                q.Answer();
                break;
            }
            for (int i = 0; i < n; i++)
            {
                jem[i] = sc.NextIntArray();
            }
            m = sc.NextInt();
            move = new int[m][];
            for (int i = 0; i < m; i++)
            {
                move[i] = new int[2];
                switch (sc.NextString())
                {
                    case "N":
                        move[i][1] = sc.NextInt();
                        continue;
                    case "E":
                        move[i][0] = sc.NextInt();
                        continue;
                    case "S":
                        move[i][1] = -sc.NextInt();
                        continue;
                    case "W":
                        move[i][0] = -sc.NextInt();
                        continue;
                    default:
                        continue;
                }
            }
            x = y = 10;
            for (int i = 0; i < n; i++)
            {
                x += move[i][0];
                y += move[i][1];
                for (int j = 0; j < n; j++)
                    if (x == jem[j][0] && y == jem[j][1])
                    {
                        count++;
                        jem[j][0] = -1; //重複して宝石を拾わないようにする
                        jem[j][1] = -1;
                    }
            }
            q.Enqueue(count == n ? "YES" : "NO");
        }
    }
}

public class AnswerCollecter<T>
{
    private Queue<T> q;

    public AnswerCollecter()
    {
        q = new Queue<T>();
    }

    public void Enqueue( T item)
    {
        q.Enqueue(item);
    }

    public void Answer()
    {
        while (q.Count != 0)
            Console.WriteLine(q.Dequeue());
    }
}

public static class Scanner
{
