int xy[4][2]={{0,-1},{1,0},{0,1},{-1,0}};
void move(int,int);
int main()
{
  int i,j,s,t;
  char ch,ch1;
  while(1){
    scanf("%d",&N);
    if(N==0)break;
    for(i=0;i<20;i++){
      for(j=0;j<20;j++){
         map[i][j]=0;
      }
    }
    ii=10;jj=10;
    for(i=0;i<N;i++){
      scanf("%d%d",&s,&t);
      map[t][s]=1;
    }
    scanf("%d",&M);
    for(i=0;i<M;i++){
      scanf("%c%c%d",&ch,&ch1,&s);
      switch(ch1){
      case 'W':
	move(0,s);
        break;
      case 'N':
        move(1,s);
	break;
      case 'E':
	move(2,s);
	break;
      default:
	move(3,s);
	break;
      }
    }
    if(N==0)printf("Yes\n");
    else printf("No\n");
    N;
  }
  return 0;
}

void move(int s,int t)
{
  int i;
  for(i=0;i<t;i++){
    ii+=xy[s][0];
    jj+=xy[s][1];
    if(map[ii][jj]==1){
      map[ii][jj]=0;
      N--;
     }
  }
}

