		map[yg][xg]=1;
	}
	scanf("%d",&m);
	x=10;  y=10;  cnt=0;
	for(i=0;i<m;i++) {
		scanf(" %c %d",&d,&i);
		if(d=='N') {
			for(j=0;j<l;j++) {
				y++;
				if(map[y][x]==1) {
					cnt++;
					map[y][x]=0;
				}
			}
		}
		if(d=='S') {
			for(j=0;j<1;j++) {
				y--;
				if(map[y][x]==1) {
					cnt++;
					map[y][x]=0;
				}
			}
		}
		if(d=='E') {
			for(j=0;j<1;j++) {
				x++;
				if(map[y][x]==1) {
					cnt++;
					map[y][x]=0;
				}
			}
		}
		if(d=='W') {
			for(j=0;j<1;j++) {
				x--;
				if(map[y][x]==1) {
					cnt++;
					map[y][x]=0;
				}
			}
		}
	}
	if(cnt==n) {
		printf("Yes\n");
	}
	else {
		printf("No\n");
	}
}

