#include <stdio.h>
#include <memory.h>

int main(void) {
	int i, j, n, m;
	int x, y, d;
	char t;
	int p_x, p_y;
	int table[21][21];

	while(1) {
		scanf("%d\n", &n);
		if(n==0) break;
		memset((void*)table, 0x00, sizeof(table));

		for(i=0;i<n;i++) {
			scanf("%d %d\n", &x, &y);
			table[y][x] = 1;
		}

		p_x = p_y = 10;
		table[p_y][p_x] = 0;
		scanf("%d\n", &m);
		for(i=0;i<m;i++) {
			scanf("%c %d\n", &t, &d);
			switch(t) {
				case 'N':
					for(j=0;j<d;j++) {
						table[++p_y][p_x] = 0;
					}
				break;
				case 'S':
					for(j=0;j<d;j++) {
						table[--p_y][p_x] = 0;
					}
				break;
				case 'W':
					for(j=0;j<d;j++) {
						table[p_y][--p_x] = 0;
					}
				break;
				case 'E':
					for(j=0;j<d;j++) {
						table[p_y][++p_x] = 0;
					}
				break;
				default:
				break;
			}
		}

		m = 0;
		for(i=0;i<21;i++) {
			for(j=0;j<21;j++) {
				if(table[i][j] != 0) {
					m = 1;
					break;
				}
			}
			if(m==1) break;
		}

		if(m==1) {
			printf("No\n");
		} else {
			printf("Yes\n");
		}

	}

	return 0;
}

#include <stdio.h>

int main() {
  int map[21][21];
  int i, j;
  int A, M;
  int x, y;
  int in1, in2;
  char c[2], a;
  int dx, dy;
  int count;
  while(1) {
    scanf("%d", &A);
    if(A == 0) {break;}
    for(i = 0; i < 20; i++) {
      for(j = 0; j < 20; j++) {
	map[i][j] = 0;
      }
    }
    for(i = 0; i < A; i++) {
      scanf("%d %d", &in1, &in2);
      map[in1][in2] = 1;
    }
    scanf("%d", &M);
    count = 0;
    x = 10;
    y = 10;
    if(map[x][y] == 1) {
      map[x][y] = 0;
      count++;
    }
    for(i = 0; i < M; i++) {
      scanf("%s %d", c, &in1);
      if(c[0] == 'N') {
	dx = 0;
	dy = 1;
      }
      if(c[0] == 'S') {
	dx = 0;
	dy = -1;
      }
      if(c[0] == 'W') {
	dx = -1;
	dy = 0;
      }
      if(c[0] == 'E') {
	dx = 1;
	dy = 0;
      }
      for(j = 0; j < in1; j++) {
	x += dx;
	y += dy;
	if(map[x][y] == 1) {
	  map[x][y] = 0;
	  count++;
	}
      }
    }
    if(count == A){
      printf("Yes\n");
    }
    else {
      printf("No\n");
    }
  }
  return 0;
}

#include <stdio.h>
int main(void){
  int i,j,locx,locy;
  int count=0;
  int x=10,y=10;//coordinate of the robot

  int n,m,l; //n: # of jewels m:# of commands l:length
  int result[100];
  while (1){
    int map[20][20];
    for (i=0;i<20;i++){
      for (j=0;j<20;j++){
	map[i][j]=0;
      }
    }
    scanf("%d",&n);
    if (n==0){
      end(result,count);
      break;
    }
    for (i=0;i<n;i++){//placing jewels
      scanf("%d %d",&locx,&locy);
      locy=20-locy;
      map[locy][locx]=1;
    }
    scanf("%d",&m);
    char d[m];//direction
    int l[m];//length
    for (i=0;i<m;i++){
      scanf("%s %d",&d[i],&l[i]);
    }
    for (i=0;i<m;i++){
      int movex=0;
      int movey=0;
      if (d[i]=='N'){
	movex=0;
	movey=-1;
      }
      if (d[i]=='S'){
	movex=0;
	movey=1;
      }
      if (d[i]=='W'){
	movex=-1;
	movey=0;
      }
      if (d[i]=='E'){
	movex=1;
	movey=0;
      }
      for (j=0;j<l[i];j++){
	x=x+movex;
	y=y+movey;
	if (map[y][x]==1)
	  map[y][x]=0;
      }
    }
    check(map,result,count);
    count++;
  }
}
int check(int m[20][20],int r[100],int c){
  int i,j;
  int n=0;
  for (i=0;i<20;i++){
    for (j=0;j<20;j++){
      if (m[i][j]==1){
	r[c]=0;
	n=1;
	break;
      }
    }
    if (n==1)
      break;
    if (i==19)
      r[c]=1;
  }
}
int end(int r[100],int c){
  int i;
  for (i=0;i<=c;i++){
    if (r[i]==0)
      printf("No\n");
    if (r[i]==1)
      printf("Yes\n");
  }
}

