	struct MAPS gem[N_MAX];
	struct COMMAND command[M_MAX];
	struct MAPS robot = { 10, 10 };
	int gembag = 0;
	int counti, countj;
	char inputline[6];
	for (counti = 0; counti < 20; counti++){
		for (countj = 0; countj < 20; countj++){
			map[counti][countj] = 0;
		}
	}


	while( 1 ){
		fgets( inputline, sizeof( inputline ), stdin );
		sscanf( inputline, "%d", &N );
		if (N == 0) break;
		for( counti = 0; counti < N; counti++){
			fgets( inputline, sizeof( inputline ), stdin );
			sscanf( inputline, "%d%d", &gem[counti].x, &gem[counti].y );
		}

		fgets( inputline, sizeof( inputline ), stdin );
		sscanf( inputline, "%d", &M );
		for ( counti = 0; counti < M; counti++ ){
			fgets( inputline, sizeof( inputline ), stdin );
			sscanf( inputline, "%c%d", &command[counti].direction, &command[counti].step );
		}


		for ( counti = 0; counti < N; counti++ ){
			map[gem[counti].y][gem[counti].x] = 1;
		}

		for(counti = 0; counti < M; counti++){
			switch (command[counti].direction)
			{
				case 'N':
					for( countj = 0; countj < command[counti].step; countj++){
						robot.y++;
						if( map[robot.y][robot.x] == 1 ){
							map[robot.y][robot.x] = 0;
							gembag++;
						}
					}
					break;
				case 'E':
					for( countj = 0; countj < command[counti].step; countj++){
						robot.x++;
						if( map[robot.y][robot.x] == 1 ){
							map[robot.y][robot.x] = 0;
							gembag++;
						}
					}
					break;
				case 'W':
					for( countj = 0; countj < command[counti].step; countj++){
						robot.x--;
						if( map[robot.y][robot.x] == 1 ){
							map[robot.y][robot.x] = 0;
							gembag++;
						}
					}
					break;
				case 'S':
					for( countj = 0; countj < command[counti].step; countj++){
						robot.y--;
						if( map[robot.y][robot.x] == 1 ){
							map[robot.y][robot.x] = 0;
							gembag++;
						}
					}
					break;
			}
		}
		if( gembag == N ){
			printf("Yes\n");
		}else{
			printf("No\n");
		}

		for ( counti = 0; counti < N; counti++ ){
			map[gem[counti].y][gem[counti].x] = 0;
		}
		gembag = 0;
		robot.x = 10;
		robot.y = 10;
	}


	return 0;

}

#include<stdio.h>
int xy[20][20];
int x, y, num;
void move(int dx, int dy, int l){
    if (l == 0)
        return;
    x+=dx; y+=dy;
    if (xy[x][y]){
        xy[x][y] = 0;
        num++;
    }
    move(dx, dy, l-1);
    return;
}
int main(){
    int n, m, i, j;
    while(1){
        for (i=0; i<20; i++)
            for (j=0; j<20; j++)
                xy[i][j] = 0;
        num = 0; x = 10; y = 10;
        scanf("%d", &n);
        if (n == 0)
            return 0;
        for (i=0; i<n; i++){
            int tx, ty;
            scanf("%d %d", &tx, &ty);
            xy[tx][ty] = 1;
        }
        scanf("%d", &m);
        for (i=0; i<m; i++){
            char op;
            int l;
            scanf(" %c %d", &op, &l);
            if     (op == 'N') move(0,1,l);
            else if(op == 'E') move(1,0,l);
            else if(op == 'S') move(0,-1,l);
            else               move(-1,0,l);
        }
        if (n == num)
            puts("Yes");
        else
            puts("No");
    }
}

