#define EXIST 1
#define EMPTY 0
#define SIZE 21
int main() {
  int N, M, i, j, x, y, amount, flag;
  int map[SIZE][SIZE];
  char way;

  for (;;) {
    for (i = 0; i < SIZE; i++)
      for (j = 0; j < SIZE; j++)
        map[i][j] = EMPTY;

    scanf("%d", &N);
    if (!N) return 0;

    for (i = 0; i < N; i++) {
      scanf("%d %d", &x, &y);
      map[SIZE - y - 1][x] = EXIST;
    }

    scanf("%d", &M);
    for (i = 0, x = 10, y = 10; i < M; i++) {
      scanf(" %c %d", &way, &amount);
      for (j = 0; j < amount; j++) {
        if (way == 'N') y++;
        if (way == 'S') y--; 
        if (way == 'E') x++;
        if (way == 'W') x--;
        map[SIZE - y - 1][x] = EMPTY;
      }
    }

    for (i = 0, flag = 0; i < SIZE; i++) 
      for (j = 0; j < SIZE; j++)
        if (map[i][j] == EXIST) flag = 1;

    if (flag)
      printf("No\n");
    else
      printf("Yes\n");
  }

  return -1;
}

