			switch (d[0]){
			  case 'N':
			    dx = 0;
				dy = 1;
				break;

			  case 'S':
			    dx = 0;
				dy = -1;
				break;

			  case 'E':
			    dx = 1;
				dy = 0;
				break;

			  case 'W':
			    dx = -1;
				dy = 0;
				break;
