			scanf("%s %d", &direc, &r);
			switch (direc){
				case 'N':
					for(i=0; i<n; i++){
						if((now_x == x[0][i])&&(now_y <= x[1][i])&&(x[1][i] <= now_y+r))
							x[2][i]=1;
					}
					now_y += r;
					break;
				case 'E':
					for(i=0; i<n; i++){
						if((now_y == x[1][i])&&(now_x <= x[0][i])&&(x[0][i] <= now_x+r))
							x[2][i]=1;
					}
					now_x += r;
					break;
				case 'S':
					for(i=0; i<n; i++){
						if((now_x == x[0][i])&&(now_y-r <= x[1][i])&&(x[1][i] <= now_y))
							x[2][i]=1;
					}
					now_y -= r;
					break;
				case 'W':
					for(i=0; i<n; i++){
						if((now_y == x[1][i])&&(now_x-r <= x[0][i])&&(x[0][i] <= now_x))
							x[2][i]=1;
					}
					now_x -= r;
					break;
			}
		}
		for(i=0; i<n; i++){
			if(x[2][i] == 0)
				printf("No\n");
		}
		if(i == n)
			printf("Yes\n");
	}
}

