#include <stdio.h>

#define N 20
#define MIN(A,B) \
  ( (A) < (B) ? (A) : (B) )
#define MAX(A,B) \
  ( (A) > (B) ? (A) : (B) )

/** Application main entry point. */
int main (
  int     argc,
  char  * argv[ ]
  )
{
  int i, j;

  for ( ; ; )
  {
    int n, m;
    int mx[ N ], my[ N ], mc[ N ] = { 0 };
    int px = 10, py = 10;
    int res = 1;

    scanf ( " %d", &n );
    if ( !n ) break ;
    for ( i = 0; i < n; ++i )
    {
      scanf ( " %d %d", mx + i, my + i );
    }

    scanf ( " %d", &m );
    for ( j = 0; j < m; ++j )
    {
      char cmd;
      int dist;
      int nx, ny;

      scanf ( " %c %d", &cmd, &dist );
      nx = px + ( cmd == 'E' ? dist : 0 )
              + ( cmd == 'W' ? -dist : 0 );
      ny = py + ( cmd == 'N' ? dist : 0 )
              + ( cmd == 'S' ? -dist : 0 );

      for ( i = 0; i < n; ++i )
      {
        if ( mc[ i ] )  continue ;

        mc[ i ] = !!( MIN( nx, px ) <= mx[ i ] && mx[ i ] <= MAX( nx, px )
                   && MIN( ny, py ) <= my[ i ] && my[ i ] <= MAX( ny, py ) );
      }

      px = nx; py = ny;
    }

    for ( i = 0; i < n; ++i )
    {
      res = res && mc[ i ];
    }
    puts ( res ? "Yes"
               : "No" );
  }

  return ( 0 );
}

