import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;

public class Main {

	private static final int fieldXSize=20+1, fieldYSize=20+1;
	private static final int initXPos=10, initYPos=10;

	private static final boolean[][] initField() {
		boolean[][] field=new boolean[fieldYSize][fieldXSize];
		for(int i=0; i<field.length; i++)
			Arrays.fill(field[i], false);
		return field;
	}

	public static void main(String[] args) throws IOException{

		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

		while(true) {

			// input gem
			int gemNum=Integer.parseInt(br.readLine());
			if(gemNum==0)
				break;
			boolean[][] field=initField();
			for(int i=0; i<gemNum; i++) {
				String[] strArray=br.readLine().split(" ");
				int x=Integer.parseInt(strArray[0]),y=Integer.parseInt(strArray[1]);
				field[y][x]=true;
			}

			// read & execute instruction
			int instNum=Integer.parseInt(br.readLine());
			int posX=initXPos, posY=initYPos;
			int gemCount=0;
			for(int i=0; i<instNum; i++) {
				String[] strArray=br.readLine().split(" ");
				int val=Integer.parseInt(strArray[1]);
				int dx=0, dy=0;
				if(strArray[0].equals("N")) dy=+1;
				if(strArray[0].equals("S")) dy=-1;
				if(strArray[0].equals("E")) dx=+1;
				if(strArray[0].equals("W")) dx=-1;
				for(int j=0; j<val; j++) {
					posX+=dx;
					posY+=dy;
					if(field[posY][posX]) {
						field[posY][posX]=false;
						gemCount++;
					}
				}
			}

			System.out.println(gemCount==gemNum?"Yes":"No");
		}

		br.close();

	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;


class Main {
	static final int MASS = 21;
	static int PX,PY;
	static int[][] map = new int[MASS][MASS];
	public static void main(String[] args) throws NumberFormatException, IOException {
		// TODO Auto-generated method stub
		BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
		while(true){
			int ts = Integer.parseInt(in.readLine());
			if(ts == 0){
				break;
			}
			PX = 10;
			PY = 10;
			for(int i= 0;i < map.length ;i++){
				Arrays.fill(map[i], 0);
			}
			for(int i = 0; i < ts ;i++){
				String[] s = in.readLine().split(" ");
				map[Integer.parseInt(s[0])][Integer.parseInt(s[1])] = 1;
			}
			int mn = Integer.parseInt(in.readLine());
			int gettr = 0;
			for(int i = 0; i < mn ;i++){
				String[] s = in.readLine().split(" ");
				gettr = gettr + move(s[0],Integer.parseInt(s[1]));
			}
			if(ts == gettr){
				System.out.println("Yes");
			}
			else {
				System.out.println("No");
			}
		}	
	}
	static int move(String h ,int n){
		int num = 0;
		if(h.equals("N")){
			for(int i = 1;i <= n;i++){
				if(map[PX][PY+i] == 1){
					num++;
					map[PX][PY+i] = 0;
				}	
			}	
			PY = PY + n;
		}
		else if(h.equals("S")){
			for(int i = 1;i <= n;i++){
				if(map[PX][PY-i] == 1){
					num++;
					map[PX][PY-i] = 0;
				}	
			}	
			PY = PY - n;
		}
		else if(h.equals("E")){
			for(int i = 1;i <= n;i++){
				if(map[PX+i][PY] == 1){
					num++;
					map[PX+i][PY] = 0;
				}	
			}
			PX = PX + n;
		}
		else {
			for(int i = 1;i <= n;i++){
				if(map[PX-i][PY] == 1){
					num++;
					map[PX-i][PY] = 0;
				}		
			}	
			PX = PX - n;
		}
		return num;
	}

}

