//map of jewelries and check and pick it 
class Jewel {
	private int px, py;

	Jewel(Integer[] p) {
		px = p[0];
		py = p[1];
	}

	public boolean match(int lx, int ly) {
		if (px == lx && py == ly)
			return true;
		else
			return false;
	}

	public void picked() {
		px = -1;
		py = -1;
	}
}

// operate robot
class Robt {
	private int lx, ly;
	private int picked = 0;

	public void locate(int x, int y, Jewel[] jewel) {
		lx = x;
		ly = y;
	}

	private void moveOne(int dx, int dy, Jewel[] jewel) {
		lx += dx;
		ly += dy;
		for (int i = 0; i < jewel.length; i++)
			if (jewel[i].match(lx, ly)) {
				picked++;
				jewel[i].picked();
			}
	}

	public void move(String com, Jewel[] jewel) {
		String[] dd = com.split(" ");
		int dist = Integer.valueOf(dd[1]);
		for (int m = 0; m < dist; m++) {
			switch (dd[0]) {
			case ("N"):
				moveOne(0, 1, jewel);
				break;
			case ("E"):
				moveOne(1, 0, jewel);
				break;
			case ("S"):
				moveOne(0, -1, jewel);
				break;
			case ("W"):
				moveOne(-1, 0, jewel);
				break;
			}
		}
	}

	public int getPicked() {
		return picked;
	}
}

