            int positionX = 10;
            int positionY = 10;
            int count = 0;

            for(int i=0; i< 21; i++){
                for(int j=0; j<21; j++){
                    field[i][j] = false;
                }
            }

            for(int i=0; i<N; i++){
                int x = scan.nextInt();
                int y = scan.nextInt();
                field[y][x] = true;
