						if(checkTresure(map,x,++y)){
							count--;
						}
					}
				}else if(op.equals("S")){
					for(int j = 0;j < t;j++){
						if(checkTresure(map,x,--y)){
							count--;
						}
					}
				}else if(op.equals("E")){
