		while(sc.hasNext()){
			int n = sc.nextInt();
			if(n==0)break;

			for(int i=0;i<=20;i++) {
				for(int j=0;j<=20;j++)
					f[i][j] = 0;
			}
			boolean ans = true;
			for(int i=0;i<n;i++) {
				int x = sc.nextInt();
				int y = sc.nextInt();
				f[x][y] = 1;
			}

			int m = sc.nextInt();
