			int y=10;
			for(int i=0;i<m;i++) {
				char a = sc.next().charAt(0);
				int d = sc.nextInt();
				switch(a) {
				case 'N':
					for(int k=y;k<=y+d;k++) {	
						f[x][k] = 0;
					}
					y += d;
					break;
				case 'E':
					for(int k=x;k<=x+d;k++) {
						f[k][y] = 0;
					}
					x += d;
					break;
				case 'S':
					for(int k=y;k>=y-d;k--) {
						f[x][k] =0;
					}
					y -= d;
					break;
				case 'W':
					for(int k=x;k>=x-d;k--) {
						f[k][y] =0;
					}
					x -= d;
					break;
				}
			}

			for(int i=0;i<=20;i++) {
				for(int j=0;j<=20;j++)
					if(f[i][j] != 0) {
						ans = false;
						break;
					}
				if(!ans)break;
			}

			System.out.println(ans);
		}

	}
}

import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		Scanner sc=new Scanner(System.in);
		boolean map[][]=new boolean[21][21];
		while(true){
			int n=sc.nextInt();
			if(n == 0) break;
			for(int i=0; i<n; i++){
				int x=sc.nextInt();
				int y=sc.nextInt();
				map[y][x]=true;
			}
			int m=sc.nextInt();
			int nx,ny;
			nx=ny=10;
			for(int i=0; i<m; i++){

				String dir=sc.next();//direction
				int move=sc.nextInt();
				//ここから移動処理。どうやら入力で範囲をはみ出ることはないらしいが、一応そこのチェックを書いておいた。
				//途中でめんどくさくなってmoveの範囲外の処理は書かなかった。
				//処理順が気持ち悪いのは方角と方向の対応を勘違いしてたため。
				if(dir.equals("S")){
					for(int j=0; j<=move; j++){
						if(0 <= ny-j){
							map[ny-j][nx]=false;
						}
					}
					ny-=move;
				}
				else if(dir.equals("E")){
					for(int j=0; j<=move; j++){
						if(nx+j < 21){
							map[ny][nx+j]=false;
						}
					}
					nx+=move;
				}
				else if(dir.equals("N")){
					for(int j=0; j<=move; j++){
						if(ny+j < 21){
							map[ny+j][nx]=false;
						}
					}
					ny+=move;
				}
				else if(dir.equals("W")){
					for(int j=0; j<=move; j++){
						if(0 <= nx-j){
							map[ny][nx-j]=false;
						}
					}
					nx-=move;
				}
//				System.out.println("ny="+ny+" nx="+nx);
			}
			//complet check
			boolean f=false;;
			for(int i=0; i<21; i++){
				for(int j=0; j<21; j++){
					if(map[i][j]){
						f=true;
						break;
					}
				}
			}
			if(f) System.out.println("No");
			else System.out.println("Yes");
		}

	}

}

import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.Channels;
import java.nio.channels.ReadableByteChannel;

public class Main {
    private static final ReadableByteChannel channel = Channels.newChannel(System.in);

    public static void main(String[] args) throws IOException {
	buf.clear();
	channel.read(buf);
	buf.flip();
	int n;
	while ((n = nextInt()) != 0) {
	    int[][] map = new int[21][21];
	    for (int i = 0; i < n; i++) {
		map[nextInt()][nextInt()] = 1;

	    }
	    int m = nextInt();
	    int x = 10;
	    int y = 10;
	    int count = 0;
	    for (int i = 0; i < m; i++) {
		char c = getChar();
		int l = nextInt();
		if (count != n) {
		    int dx = 0;
		    int dy = 0;
		    if (c == 'N') {
			dy = 1;
		    } else if (c == 'E') {
			dx = 1;
		    } else if (c == 'S') {
			dy = -1;
		    } else if (c == 'W') {
			dx = -1;
		    }
		    for (int j = 0; j < l; j++) {
			count += map[x][y];
			map[x][y] = 0;
			x += dx;
			y += dy;
		    }
		    count += map[x][y];
		    map[x][y] = 0;
		}
	    }
	    if (count == n) {
		System.out.println("Yes");
	    } else {
		System.out.println("No");
	    }
	}
    }

    private static char getChar() throws IOException {
	int c = ' ';
	while (c < 'E' || 'W' < c) {
	    c = buf.get();
	}
	return (char) c;
    }

    static ByteBuffer buf = ByteBuffer.allocateDirect(10000000);

    private static int nextInt() throws IOException {
	char c = (char) buf.get();
	while ((c < '0' || '9' < c) && c != '-') {
	    c = (char) buf.get();
	}
	if (c == '-') {
	    return -nextInt();
	}
	int result = 0;
	while ('0' <= c && c <= '9') {
	    result = result * 10 + (c - '0');
	    c = (char) buf.get();
	}
	return result;
    }
}

