					for(int i = 0; i <= itmp; i++) {
						roboflag[x][y+i] = true;
					}
					y += itmp;
				} else if(stmp.equals("E")) {
					for(int i = 0; i <= itmp; i++) {
						roboflag[x+i][y] = true;
					}
					x += itmp;
				} else if(stmp.equals("S")) {
					for(int i = 0; i <= itmp; i++) {
						roboflag[x][y-i] = true;
					}
					y -= itmp;
				} else if(stmp.equals("W")) {
					for(int i = 0; i <= itmp; i++) {
						roboflag[x-i][y] = true;
					}
					x -= itmp;
				}
			}
			//map();
			for(int i = 0; i <= gemNum; i++) {
				if(i == gemNum) {
					System.out.println("Yes");
					break;
				}
