		boolean flag;
		for(int i = 0; i <= 20; i++) System.out.print(i + " ");
		System.out.println();
		for(int i = 0; i <= 20;i++) {
			System.out.print(i + " ");
			for(int j = 0; j<= 20; j++) {
			flag = false;
				for(int m = 0; m < gemNum; m++) {
					if(gems[m][0] == i && gems[m][1] == j) {
						System.out.print("- ");
						flag = true;
					}
				}
				if(!flag)	System.out.print("+ ");
			}
			System.out.println();
		}
