public class Main {

	static Scanner sc = new Scanner(System.in);
	static int N;
	static int[] DR = { 1, 0, -1, 0 };
	static int[] DC = { 0, 1, 0, -1 };

	static boolean solve() {
		int[][] map = new int[21][21];
		int all = 0;
		for (int i = 0; i < N; ++i) {
			int x = sc.nextInt();
			int y = sc.nextInt();
			map[y][x] = 1;
			all++;
		}
		int r = 10;
		int c = 10;
