		int M = sc.nextInt();
		for (int i = 0; i < M; ++i) {
			int d = "NESW".indexOf(sc.next());
			int l = sc.nextInt();
			for (int j = 0; j < l; ++j) {
				r += DR[d];
				c += DC[d];
				collect += map[r][c];
				map[r][c] = 0;
			}
		}
		return collect == all;
	}

