	public static void main(String args[]){
		try(Scanner sc=new Scanner(System.in)){
			while(sc.hasNext()) {
				int n=sc.nextInt();
				if(n==0) break;
				boolean[][] gems=new boolean[21][21];
				for(int i=0; i<n; i++) {
					int a=sc.nextInt();
					int b=sc.nextInt();
					gems[a][b]=true;
				}
				int m=sc.nextInt();
				int nowx=10;
				int nowy=10;
				int nextx=0;
				int nexty=0;
				for(int i=0; i<m; i++) {
					String s=sc.next();
					int num=sc.nextInt();
					if(s.equals("N")) {
						nextx=nowx;
						nexty=nowy+num;
					}
					else if(s.equals("E")) {
						nextx=nowx+num;
						nexty=nowy;
					}
					else if(s.equals("S")) {
						nextx=nowx;
						nexty=nowy-num;
					}
					else if(s.equals("W")) {
						nextx=nowx-num;
						nexty=nowy;
					}
					for(int x=Math.min(nowx, nextx); x<=Math.max(nowx, nextx); x++) {
						for(int y=Math.min(nexty, nowy); y<=Math.max(nexty, nowy); y++) {
							if(gems[x][y]) {
								gems[x][y]=false;
							}
						}
					}
					nowx=nextx;
					nowy=nexty;
				}
				int flag=0;
				for(int i=0; i<=20; i++) {
					for(int j=0; j<=20; j++) {
						if(gems[i][j]) {
							flag=1;
						}
					}
				}
				System.out.println((flag==1)? "No":"Yes");
			}
		}
	}
}

import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);

		while (true) {
			int[][] field = new int[21][21];

			int robotPositionYoko=10;
			int robotPositionTate=10;

			int jueleNumber = scan.nextInt();
			int juelePositionYoko;
			int juelePositionTate;
			if (jueleNumber == 0)
				break;

			for (int i = 0; i < jueleNumber; i++) {
				juelePositionYoko = scan.nextInt();
				juelePositionTate = scan.nextInt();
				field[juelePositionYoko][juelePositionTate] = 1;
			}

			int order = scan.nextInt();
			char direction;
			int dirNumber;
			for (int i = 0; i < order; i++) {
				direction = scan.next().charAt(0);
				dirNumber = scan.nextInt();
				switch (direction) {
				case 'N':
					for(int j=0;j<dirNumber;j++){
						robotPositionTate++;
						if(field[robotPositionYoko][robotPositionTate]==1)field[robotPositionYoko][robotPositionTate]=0;
					}
					break;
				case 'E':
					for(int j=0;j<dirNumber;j++){
						robotPositionYoko++;
						if(field[robotPositionYoko][robotPositionTate]==1)field[robotPositionYoko][robotPositionTate]=0;
					}
					break;
				case 'S':
					for(int j=0;j<dirNumber;j++){
						robotPositionTate--;
						if(field[robotPositionYoko][robotPositionTate]==1)field[robotPositionYoko][robotPositionTate]=0;
					}
					break;
				case 'W':
					for(int j=0;j<dirNumber;j++){
						robotPositionYoko--;
						if(field[robotPositionYoko][robotPositionTate]==1)field[robotPositionYoko][robotPositionTate]=0;
					}
					break;
				}

			}
			int flag=0;
			for(int i=0;i<21;i++){
				for(int j=0;j<21;j++){
					if(field[i][j]==1)flag=1;
				}
			}
			if(flag==0){
				System.out.println("Yes");
			}else{
				System.out.println("No");
			}

		}
	}
}

