		juels = new ArrayList<int[]>();
		int x = 10;
		int y = 10;
		while (scan.hasNext()) {
			n = scan.nextInt();
			if (n == 0) {
				scan.close();
				break;
			}
			//宝石の座標をセット
			for (int i = 0; i < n; i++) {
				juels.add(new int[] { scan.nextInt(), scan.nextInt() });
			}
			event = scan.nextInt();
			String direction;

			int len;
			int count=0;
			for (int i = 0; i < event; i++) {
				direction = scan.next();
				len = scan.nextInt();

				for (int j = 0; j < len; j++) {
					if (direction.equals("S")) {
						y -= 1;
					} else if (direction.equals("N")) {
						y += 1;
					} else if (direction.equals("W")) {
						x -= 1;
					} else if (direction.equals("E")) {
						x += 1;
					}
					count+=checkjuel(juels, x, y);
				}

			}
			if(count==n) {
				System.out.println("Yes");
			}else {
				System.out.println("No");
			}

		}
	}

	private static int checkjuel(ArrayList<int[]> juels, int x, int y) {
		for (int pos = 0; pos < juels.size(); pos++) {

			if (juels.get(pos)[0] == x && juels.get(pos)[1] == y) {
				return 1;
			}
		}
		return 0;
	}
}

import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.HashMap;
import java.util.InputMismatchException;
import java.util.NoSuchElementException;

public class Main {

	static PrintWriter out;
	static InputReader ir;
	static boolean debug = false;

	static void solve() {
		HashMap<String, int[]> dir = new HashMap<>();
		dir.put("N", new int[] { 1, 0 });
		dir.put("E", new int[] { 0, 1 });
		dir.put("S", new int[] { -1, 0 });
		dir.put("W", new int[] { 0, -1 });
		for (;;) {
			int n = ir.nextInt();
			if (n == 0)
				return;
			boolean[][] gems = new boolean[21][21];
			boolean[][] visited = new boolean[21][21];
			visited[10][10] = true;
			int cnt = 0;
			for (int i = 0; i < n; i++) {
				int x = ir.nextInt();
				int y = ir.nextInt();
				gems[y][x] = true;
			}
			int m = ir.nextInt();
			int[] now = new int[] { 10, 10 };
			while (m-- > 0) {
				String com = ir.next();
				int t = ir.nextInt();
				for (int i = 0; i < t; i++) {
					for (int j = 0; j < 2; j++)
						now[i] += dir.get(com)[j];
					if (!visited[now[0]][now[1]] && gems[now[0]][now[1]])
						cnt++;
					visited[now[0]][now[1]] = true;
				}
			}
			out.println(cnt == n ? "Yes" : "No");
		}
	}

	public static void main(String[] args) {
		ir = new InputReader(System.in);
		out = new PrintWriter(System.out);
		solve();
		out.flush();
	}

	static class InputReader {

		private InputStream in;
		private byte[] buffer = new byte[1024];
		private int curbuf;
		private int lenbuf;

		public InputReader(InputStream in) {
			this.in = in;
			this.curbuf = this.lenbuf = 0;
		}

		public boolean hasNextByte() {
			if (curbuf >= lenbuf) {
				curbuf = 0;
				try {
					lenbuf = in.read(buffer);
				} catch (IOException e) {
					throw new InputMismatchException();
				}
				if (lenbuf <= 0)
					return false;
			}
			return true;
		}

		private int readByte() {
			if (hasNextByte())
				return buffer[curbuf++];
			else
				return -1;
		}

		private boolean isSpaceChar(int c) {
			return !(c >= 33 && c <= 126);
		}

		private void skip() {
			while (hasNextByte() && isSpaceChar(buffer[curbuf]))
				curbuf++;
		}

		public boolean hasNext() {
			skip();
			return hasNextByte();
		}

		public String next() {
			if (!hasNext())
				throw new NoSuchElementException();
			StringBuilder sb = new StringBuilder();
			int b = readByte();
			while (!isSpaceChar(b)) {
				sb.appendCodePoint(b);
				b = readByte();
			}
			return sb.toString();
		}

		public int nextInt() {
			if (!hasNext())
				throw new NoSuchElementException();
			int c = readByte();
			while (isSpaceChar(c))
				c = readByte();
			boolean minus = false;
			if (c == '-') {
				minus = true;
				c = readByte();
			}
			int res = 0;
			do {
				if (c < '0' || c > '9')
					throw new InputMismatchException();
				res = res * 10 + c - '0';
				c = readByte();
			} while (!isSpaceChar(c));
			return (minus) ? -res : res;
		}

		public long nextLong() {
			if (!hasNext())
				throw new NoSuchElementException();
			int c = readByte();
			while (isSpaceChar(c))
				c = readByte();
			boolean minus = false;
			if (c == '-') {
				minus = true;
				c = readByte();
			}
			long res = 0;
			do {
				if (c < '0' || c > '9')
					throw new InputMismatchException();
				res = res * 10 + c - '0';
				c = readByte();
			} while (!isSpaceChar(c));
			return (minus) ? -res : res;
		}

		public double nextDouble() {
			return Double.parseDouble(next());
		}

		public int[] nextIntArray(int n) {
			int[] a = new int[n];
			for (int i = 0; i < n; i++)
				a[i] = nextInt();
			return a;
		}

		public long[] nextLongArray(int n) {
			long[] a = new long[n];
			for (int i = 0; i < n; i++)
				a[i] = nextLong();
			return a;
		}

		public char[][] nextCharMap(int n, int m) {
			char[][] map = new char[n][m];
			for (int i = 0; i < n; i++)
				map[i] = next().toCharArray();
			return map;
		}
	}

	static void tr(Object... o) {
		if (debug)
			out.println(Arrays.deepToString(o));
	}
}

