				String op = scan.next();
				t = scan.nextInt();
				if(t == 0){
					continue;
				}
				if(op.equals("N")){
					for(int j = 0;j < t;j++){
						if(checkTresure(map,x,++y)){
							count--;
							changeMap(map,x,y);
						}
					}
				}else if(op.equals("S")){
					for(int j = 0;j < t;j++){
						if(checkTresure(map,x,--y)){
							count--;
							changeMap(map,x,y);
						}
					}
				}else if(op.equals("E")){
					for(int j = 0;j < t;j++){
						if(checkTresure(map,++x,y)){
							count--;
							changeMap(map,x,y);
						}
					}
				}else if(op.equals("W")){
					for(int j = 0;j < t;j++){
