				target = new Point2D[n];
				for(int i=0;i<n;i++)target[i] = new Point2D.Double(in.nextDouble(), in.nextDouble());
				boolean sw = solve();
				System.out.println(sw? "Yes":"No");
			}
		}
	}

}

