				}

			}
		}

		//System.out.println(answer);

	}
	/**
	* ロボットが通った座標を0に置き換える
	* @param direction
	* @param distance
	*/
	public void deleteGem( char direction , int distance ){

		for( int i = 0 ; i < arySearchArea.length ; i++ ){
			for( int j = 0 ; j < arySearchArea[i].length ; j++ ){


				//ロボットの場所を見つけ
				if( arySearchArea[i][j] == 2 ){

					// Nだった場合
					if( direction == 'N'){

						for( int k = 0 ; k < distance ; k++ ){

							arySearchArea[i][j] = 0;
							arySearchArea[i][j+1] = 0;

						}

						arySearchArea[i][j + distance] = 2;
						break;

					// Wだった場合
					}else if( direction == 'W'){

						for( int k = 0 ; k < distance ; k++ ){

							arySearchArea[i][j] = 0;
							arySearchArea[i-1][j] = 0;
						}

						arySearchArea[i - distance][j] = 2;
						break;

					// Sだった場合
					}else if( direction == 'S'){

						for( int k = 0 ; k < distance ; k++ ){

							arySearchArea[i][j] = 0;
							arySearchArea[i][j-1] = 0;
						}

						arySearchArea[i][j - distance] = 2;
						break;


					// Eだった場合
					}else if( direction == 'E'){

						for( int k = 0 ; k < distance ; k++ ){

							arySearchArea[i][j] = 0;
							arySearchArea[i+1][j] = 0;
						}

						arySearchArea[i + distance][j] = 3;
						break;

					}

				}
			}
		}

		//E救済用
		for( int i = 0 ; i < arySearchArea.length ; i++ ){
			for( int j = 0 ; j < arySearchArea[i].length ; j++ ){

				if( arySearchArea[i][j] == 3 ){
					arySearchArea[i][j] = 2;
				}

			}
		}

	}

