			for(int i=0;i<n;i++){
				String dir = sc.next();
				int k = sc.nextInt();
				if(dir.equals("N")){
					for(int j=1;j<=k;j++){
						rob[rx][ry+j]++;
						if(rob[rx][ry+j]==gem[rx][ry+j]){
							last++;
